import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import { Typography, Box } from '@material-ui/core';
import Nav from './Nav'


const useStyles = makeStyles(theme => ({
    mainContainer: {
        background: "green",
    },
    timeLine: {
        position: 'relative',
        padding: '1rem',
        margin: '0 auto',
        '&:before': {
            content: "''",
            position: 'absolute',
            height: '100%',
            border: '1px solid tan',
            right: '40px',
            top: 0
        },
        '&:after': {
            content: "''",
            display: 'table',
            clear: 'both'
        },
        [theme.breakpoints.up('md')]:{
            padding: '2rem',
            '&:before': {
                left: 'calc(50% - 1px)',
                right: 'auto'
            }
        }
    },
    timeLineYear: {
        textAlign: 'center',
        macWidth: '9rem',
        margin: '0 3rem 0 auto',
        fontSize: '1.9rem',
        background: 'tomato',
        color: 'white',
        lineHeight: 1,
        padding: '0.5rem 0 1rem',
        '&:before': {
            display: 'none'
        },
        [theme.breakpoints.up('md')]: {
            textAlign: 'center',
            margin: '0 auto',
            "&:nth-of-type(2n)": {
                float: 'none',
                margin: '0 auto'
            },
            "&:nth-of-type(2n):before": {
                display: 'none'
            }

        }
    }
}))



const About = () => {
    const classes = useStyles();
    return (
    <>
        <Nav/>
        <Box component='div' className={classes.mainContainer} >
            <Typography varient='h3' align='center'>
                ABOUT ME: IAN JOHNSON
            </Typography>
            <Box component='div' className={classes.timeLine}>
                <Typography varient='h2' className={classes.timeLineYear}>1994</Typography>
            </Box>
        </Box>
    </>
    )
}

export default About
